import axios  from 'axios';
import React, {Component} from 'react';
class Authors extends Component{
    constructor(props) {
        super(props);
        this.state = {
            username: '',
            password:'',
            token:'',
        }
    }
    getAuthors =  async (data) =>{
        var {username} = data;
        var {password} = data;
        let url = 'http://127.0.0.1:8000/book/authors/';
        var token = 'Token ' + localStorage.getItem('token')
        console.log('token', token)

        let options = {
            method: 'GET',
            url : url,
            header: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
                'Authorization': token
            },
            credentials: "same-origin",
        };

        await axios({...options}).then((res)=>{
            if (res.data) {
              console.log(res.data)
            }
        }).catch((err)=>{
            console.log(err)
            console.log("LOGIN FAIL, PLEASE TRY AGAIN")
        })


    }
    componentDidMount =() =>{
        this.getAuthors();
    }

    render(){
        return(
            <div>
                <table className="table table-striped table-bordered table-sm tinymask ">
                <thead className="thead-dark">
                    <tr>
                    <th>ID</th>
                    <th>First Name</th>
                    <th>Last Name</th>
                    <th>Email</th>
                    <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                </tbody>
                </table>
                <center><a href className="btn btn-primary">Add New Record</a></center>
        </div>
        );
    }
}

export default Authors;
