import React, { Component } from 'react';
import { render } from "react-dom"

class App extends Component
{
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            loaded: false,
            placeholder: "Loading"
        };
    }

    componentDidMount() {
        fetch("http://127.0.0.1:8000/book/authors/")
            .then(response => {
                console.log()
                if (response.status > 400) {
                    return this.setState(() => {
                        return { placeholder: "Something went wrong!" };
                    });
                }
                return response.json();
            })
            .then(data => {
                this.setState(() => {
                    return {
                        data,
                        loaded: true
                    };
                });
            });
    }

    render(){
        console.log(11, this.state.data)
        return (
            <ul>
                {this.state.data.map(authors => {
                return (
                    <li key = {authors.id}>
                        {authors.first_name} - {authors.last_name} - {authors.email}
                    </li>
                );
            })}
            </ul>      
        );
    }
}

export default App;

